import React, { useState, useEffect } from 'react';
import './Login.css';
import './Home.css'; // Importar estilos da página Home
import Home from './Home';
import token from '../function/oauth.js'

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {

    if (username === 'enrico' && password === 'password') {
      setLoggedIn(true);
      ;
    }
  };

  useEffect(() => {
    // Se estiver logado, exibir a página Home
    if (loggedIn) {
      document.body.classList.add('logged-in'); // Adicionar classe CSS para ajuste de estilo
    } else {
      document.body.classList.remove('logged-in'); // Remover classe CSS se não estiver logado
    }
  }, [loggedIn]);

  return (
    <div className="login-container">
      <h2>Login de Usuário</h2>
      {loggedIn ? (
        <Home />
      ) : (
        <div className="login-form">
          <input
            type="text"
            placeholder="Nome de usuário"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Senha"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleLogin}>Entrar</button>
        </div>
      )}
    </div>
  );
};

export default Login;
