import config from '../config/config.js';
import token from './oauth.js';

async function getFruitsByName(name) {
  try {
    const response = await fetch(`${config.apiUrl}/${name}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    if (!response.ok) {
      throw new Error('Erro do response.');
    }

    const data = await response.json();
    return data;
  } catch (error) {
    console.error(error);
    throw new Error('Erro ao obter as frutas desejadas por nome');
  }
}

export default getFruitsByName;
