import React, { useEffect, useState } from 'react';
import getFruitsByName from '../function/getFruitsByName';
import './Fruits.css';

function FruitList({ fruits }) {
  return (
    <ul className="fruit-list">
      {fruits.map((fruit, index) => (
        <li key={index} className="fruit-list-item">
          {fruit.name}
        </li>
      ))}
    </ul>
  );
}

function Fruits() {
  const [fruits, setFruits] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    async function fetchData() {
      try {
        if (searchTerm === '') {
          // Fetch all fruits if searchTerm is empty
          // Replace this logic with your actual code to fetch all fruits
          const allFruits = await get();
          setFruits(allFruits);
        } else {
          const data = await getFruitsByName(searchTerm);
          setFruits(data);
        }
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, [searchTerm]);

  function handleSearchTermChange(event) {
    setSearchTerm(event.target.value);
  }

  return (
    <div className="home-container">
      <h1 className="home-header">Lista de Frutas</h1>
      <input
        type="text"
        placeholder="Pesquisar fruta por nome"
        value={searchTerm}
        onChange={handleSearchTermChange}
      />
      <FruitList fruits={fruits} />
    </div>
  );
}

export default Fruits;
