import React, { useEffect, useState } from 'react';
import getFruits from '../function/frutasController';
import './Fruits.css';
import { useNavigate } from 'react-router-dom'; // Importe o hook de roteamento

function FruitList({ fruits }) {
  // Componente FruitList não foi alterado
}

function Fruits() {
  const [allFruits, setAllFruits] = useState([]);
  const [filteredFruits, setFilteredFruits] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  const navigate = useNavigate(); // Obtenha a função de navegação

  // Adicione o estado de autenticação
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    async function fetchData() {
      try {
        const data = await getFruits();
        setAllFruits(data);
        setFilteredFruits(data);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();

    // Verifique se o usuário está autenticado aqui
    if (!authenticated) {
      navigate('/login'); // Redirecione para a página de login se não autenticado
    }
  }, [authenticated, navigate]);

  useEffect(() => {
    const filtered = allFruits.filter(fruit =>
      fruit.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredFruits(filtered);
  }, [searchTerm, allFruits]);

  function handleSearchTerm(event) {
    setSearchTerm(event.target.value);
  }

  return (
    <div className="home-container">
      <h1 className="home-header">Lista de Frutas</h1>
      <input
        type="text"
        placeholder="Pesquisar fruta por nome"
        value={searchTerm}
        onChange={handleSearchTerm}
      />
      <FruitList fruits={filteredFruits} />
    </div>
  );
}

export default Fruits;
