import React, { useState} from 'react';
import './Login.css';
import './Home.css'; // Importar estilos da p치gina Home
import { useNavigate } from 'react-router-dom';
import getAccessToken from '../function/oauth.js';


export default function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {   // Verifica o Login

    if (username === 'enrico' && password === '8uGm0&H&') {
      navigate('/home');
    } else {
      console.log('Login falhou');
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await getAccessToken(username, password);
      handleLogin();
      console.log('Login bem-sucedido');
    } catch (error) {
      console.log('Falha no login');
    }
  };

  return (
    <div className="login-container">
      <h2>Login de Usu치rio</h2>
      <div className="login-form">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Nome de usu치rio"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Senha"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Entrar</button>
        </form>
      </div>
    </div>
  );
};
