import React, { useEffect, useState } from 'react';
import getFruits from '../function/frutasController';
import './Fruits.css';
import { useNavigate } from 'react-router-dom';

function Fruits({ authenticated }) {
  const [allFruits, setAllFruits] = useState([]);
  const [filteredFruits, setFilteredFruits] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

// function FruitList({ fruits }) {
//   return (
//     <ul className="fruit-list">
//       {fruits.map((fruit, index) => (
//         <li key={index} className="fruit-list-item">
//           {fruit.name}
//         </li>
//       ))}
//     </ul>
//   );
// }

  useEffect(() => {
    async function fetchData() {
      try {
        // const token = localStorage.getItem('token')
        // console.log(to);
        const data = await getFruits();
        console.log(data);
        setAllFruits(data);
        setFilteredFruits(data);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();

    if (!authenticated) {    // Verifica se o usu치rio est치 autenticado 
      navigate('/login'); 
    }
  }, [authenticated, navigate]);

  // useEffect(() => {
  //   const filtered = allFruits.filter(fruit =>
  //     fruit.name.toLowerCase().includes(searchTerm.toLowerCase())
  //   );
  //   setFilteredFruits(filtered);

  // }, [searchTerm, allFruits]);

  function handleSearchTerm(event) {
    setSearchTerm(event.target.value);
  }

  return (
    <div className="home-container">
      <h1 className="home-header">Lista de Frutas</h1>
      <input
        type="text"
        placeholder="Pesquisar fruta por nome"
        value={searchTerm}
        onChange={handleSearchTerm}
      />
      {allFruits[0]}
    </div>
  );
}

export default Fruits;
