import https from 'https'; 
import config from '../config/config.js';


async function getAccessToken() {  // Função para gerar o token de acesso
  try {
    const response = await fetch(config.authUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      body: new URLSearchParams({
        grant_type: 'password', 
        client_id: config.clientId,
        client_secret: config.clientSecret,
        username: config.username,
        password: config.password
      }),
      agent: new https.Agent({ rejectUnauthorized: false }) // Ignora a verificação do certificado
    });

    if (!response.ok) {
      throw new Error('Erro ao obter o token.');
    }

    const responseBody = await response.json();
    const token = responseBody.access_token;
    console.log(responseBody);

    return token; // Retorna o token de acesso
  } catch (err) {
    console.error(err);
    throw new Error('Erro ao obter o token de acesso.');
  }
}

const token = await getAccessToken();
export default token;
