import React, { useState} from 'react';
import './Login.css';
import './Home.css'; // Importar estilos da p치gina Home
import { useNavigate } from 'react-router-dom';
import getAccessToken from '../function/oauth.js';


export default function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {   // Verifica o Login

    if (username === 'enrico' && password === '8uGm0&H&') {
      navigate('/home');
    } else {
      console.log('Login falhou');
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await getAccessToken(username, password);
      handleLogin();
      console.log('Login bem-sucedido');
    } catch (error) {
      console.log('Falha no login');
    }
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <form onSubmit={handleSubmit}>
        <h2>Login de Usu치rio</h2>
          <div className="input-container">
            <label className="input-label">Nome de usu치rio</label>
            <input
              type="text"
              className="input-field"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="input-container">
            <label className="input-label">Senha</label>
            <input
              type="password"
              className="input-field"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="submit-button">Entrar</button>
        </form>
      </div>
    </div>
  );
};
